import React from 'react';
import { WidgetApiProvider } from '@matrix-widget-toolkit/react';
import { WidgetApi } from 'matrix-widget-api';
import RatingWidget from './components/RatingWidget';

// Создаём Promise для Widget API
const widgetApiPromise = Promise.resolve(new WidgetApi());

function App() {
  return (
    <WidgetApiProvider
      widgetApiPromise={widgetApiPromise}
      widgetRegistration={{
        name: 'Оценка бота',
        type: 'com.comind.bot-rating',
        data: { 
          title: 'Оценка бота',
          description: 'Виджет для оценки ответов бота звёздочками'
        },
      }}
    >
      <RatingWidget />
    </WidgetApiProvider>
  );
}

export default App;
